  AND T.DECORRENZA=TO_DATE('0101'||TO_CHAR(AnnoNuovo-1),'DDMMYYYY');

  update T002_QUERYPERSONALIZZATE  
  set RIGA = '"31/01/'||TO_CHAR(AnnoNuovo)||'","'||TO_CHAR(MassimaleAnnuoIndArt42)||'",*'
